# Map keywords to known categories
CATEGORY_ALIASES = {{
    "photographer": "Photography",
    "photos": "Photography",
    "decorator": "Event Decorators",
    "venue": "Venue",
    "balloons": "Balloons",
    "dj": "Entertainment",
    "music": "Entertainment",
    "baker": "Bakery",
    "cake": "Bakery",
    "cupcake": "Bakery",
    "bounce": "Bounce Rentals",
    "inflatable": "Bounce Rentals",
    "bar": "Mobile Bars",
    "drinks": "Mobile Bars",
    "flowers": "Florists / Flowers",
    "florist": "Florists / Flowers",
    "setup": "Hospitality (set up/ clean up)"
}}

# Location normalization map (simplified)
def normalize_location(input_location):
    input_location = input_location.lower()
    all_locations = list(set([v["Location"].lower() for v in VENDORS]))
    match = difflib.get_close_matches(input_location, all_locations, n=1)
    return match[0] if match else None

# Match vendors by intent
def find_vendors(user_message):
    user_message = user_message.lower()
    matches = []

    # Match category based on known aliases
    matched_category = None
    for keyword, category in CATEGORY_ALIASES.items():
        if keyword in user_message:
            matched_category = category
            break

    # Match location if mentioned
    matched_location = normalize_location(user_message)

    for v in VENDORS:
        if matched_category and matched_category.lower() not in v["Category"].lower():
            continue
        if matched_location and matched_location not in v["Location"].lower():
            continue
        matches.append(v)

    if not matches and matched_category:
        for v in VENDORS:
            if matched_category.lower() in v["Category"].lower():
                matches.append(v)

    return matches

@app.route("/chat", methods=["POST"])
def chat():
    user_input = request.json.get("message", "")
    results = find_vendors(user_input)

    if not results:
        return jsonify({{"response": "Sorry, I couldn't find any matching vendors."}})

    response = "Here are a few options I found for you:\n"
    for v in results[:3]:
        response += f"- {v['Business Name']} ({v['Category']}, {v['Location']})\n  Link: {v['Link']}\n"
    response += "\nNote: These are suggestions based on our current vendor list and are not official quotes."

    return jsonify({{"response": response}})

